import React, { useState } from "react";
import './Dashboard.css'; // For custom styling

const Dashboard = () => {
  const [categories, setCategories] = useState([
    {
      id: 1,
      name: "CSPM Executive Dashboard",
      widgets: [
        { id: 1, name: "Widget 1", content: "Random text for widget 1" },
        { id: 2, name: "Widget 2", content: "Random text for widget 2" },
      ],
    },
    {
      id: 2,
      name: "Another Category",
      widgets: [],
    },
  ]);

  const [widgetName, setWidgetName] = useState("");
  const [widgetContent, setWidgetContent] = useState("");

  // Add new widget to a category
  const addWidget = (categoryId) => {
    if (widgetName && widgetContent) {
      setCategories((prevState) =>
        prevState.map((category) =>
          category.id === categoryId
            ? {
                ...category,
                widgets: [
                  ...category.widgets,
                  { id: Date.now(), name: widgetName, content: widgetContent },
                ],
              }
            : category
        )
      );
      setWidgetName("");
      setWidgetContent("");
    } else {
      alert("Please enter both widget name and content");
    }
  };

  // Remove widget
  const removeWidget = (categoryId, widgetId) => {
    setCategories((prevState) =>
      prevState.map((category) =>
        category.id === categoryId
          ? {
              ...category,
              widgets: category.widgets.filter((widget) => widget.id !== widgetId),
            }
          : category
      )
    );
  };

  return (
    <div className="dashboard">
      {categories.map((category) => (
        <div className="category" key={category.id}>
          <h2>{category.name}</h2>
          <div className="widget-form">
            <input
              type="text"
              value={widgetName}
              onChange={(e) => setWidgetName(e.target.value)}
              placeholder="Enter Widget Name"
            />
            <input
              type="text"
              value={widgetContent}
              onChange={(e) => setWidgetContent(e.target.value)}
              placeholder="Enter Widget Content"
            />
            <button onClick={() => addWidget(category.id)}>Add Widget</button>
          </div>

          <div className="widget-grid">
            {category.widgets.map((widget) => (
              <div className="widget" key={widget.id}>
                <h3>{widget.name}</h3>
                <p>{widget.content}</p>
                <button
                  className="remove-btn"
                  onClick={() => removeWidget(category.id, widget.id)}
                >
                  Remove
                </button>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
